import React, {useState, useEffect, Children} from "react";
import Collapsible from "react-collapsible";
import App from "../App";
import { Button } from "./Button";

export function Titles ({name}) {
    return (
        <h1 className="heading" style={{background: "#8adef8"}}>{name}</h1>
    )
}

export function AllCourses() {
    const storage = window.localStorage;
    const [toRender, setToRender] = useState(window.localStorage.getItem("toRender"));

    useEffect(() => {
        console.log(window.localStorage.getItem("toRender"))
        window.localStorage.setItem('toRender', toRender);
    })

    function handleComponent(component) {
        storage.setItem("toRender", component);
        setToRender(component);
        console.log(storage.getItem("toRender"));
    }

    if(toRender === "App") {
        return(<App />);
    }

    return (
        <div >
            <Button handleClick={() => handleComponent("App")} children="Back"/>
            <div className="box">
                <Collapsible trigger={<Titles name="Fall 2022"/>}>
                    <div className="textBox">
                        <h1 className="heading">
                            CIS 2400 Computer Systems
                        </h1>
                        <h1 className="heading">
                            PSYC 3231 Consciousness
                        </h1>
                        <h1 className="heading">
                            MKTG 1010 Intro. to Marketing
                        </h1>
                        <h1 className="heading">
                            MKTG 2340 Systematic Approach to Creativity
                        </h1>
                    </div>
                </Collapsible>
                <br/>

                <Collapsible trigger={<Titles name="Spring 2022"/>}>
                    <div className="textBox">
                        <h1 className="heading"> 
                            MKTG 2370 Brain Science for Business
                        </h1>
                        <h1 className="heading"> 
                            CIS 3500 Software Design/Engineering
                        </h1>
                        <h1 className="heading"> 
                            CIS 2620 Automata, Computability, and Complexity
                        </h1>
                        <h1 className="heading"> 
                            LING 1510 Language and Thought
                        </h1>
                    </div>
                </Collapsible>
                <br/>

                <Collapsible trigger={<Titles name="Fall 2021"/>}>
                    <p>Course</p>
                </Collapsible>
                <br/>

                <Collapsible trigger={<Titles name="Spring 2022"/>}>
                    <p>Course</p>
                </Collapsible>
                <br/>

                <Collapsible trigger={<Titles name="Fall 2020"/>}>
                    <p>Course</p>
                </Collapsible>
                <br/>






            </div>
        </div>
    );
}
